mapper.update:
INSERT INTO `count` (uid, count_key, obj_id, count_value)
VALUES (#{uid}, #{key}, #{objId}, #{value})
ON DUPLICATE KEY UPDATE count_value = count_value + VALUES(count_value);

mapper.select:
select obj_id,count_key,count_value from `count`
where obj_id=#{objId} and obj_type=#{objType} and count_key in

    #{key}
